mapper.select:
SELECT COUNT(*) as cnt 
from multiboardtbl A, articletbl B 
WHERE A.board_no = B.board_no AND A.board_no = #{board_no}

SELECT B.* FROM multiboardtbl A, articletbl B
WHERE A.board_no = B.board_no AND B.article_title LIKE CONCAT('%', #{keyword}, '%') AND B.article_deleteyn = 'N' AND A.board_no = #{board_no} ORDER BY B.article_date DESC LIMIT #{pageCon}, #{pageList}
WHERE B.article_content LIKE CONCAT('%', #{keyword}, '%') AND B.article_deleteyn = 'N' AND A.board_no = #{board_no} ORDER BY B.article_date DESC LIMIT #{pageCon}, #{pageList}
WHERE article_writer LIKE CONCAT('%', #{keyword}, '%') AND B.article_deleteyn = 'N' AND A.board_no = #{board_no} ORDER BY B.article_date DESC LIMIT #{pageCon}, #{pageList}
WHERE B.article_title LIKE CONCAT('%', #{keyword}, '%') OR B.article_content LIKE CONCAT('%', #{keyword}, '%') OR B.article_writer LIKE CONCAT('%', #{keyword}, '%') AND A.board_no = #{board_no} AND B.article_deleteyn = 'N' LIMIT #{pageCon}, #{pageList}

mapper.update:
UPDATE ARTICLETBL 
	SET article_title		= #{article_title}, 
		article_content 	= #{article_content}, 
		article_userid 		= #{article_userid}
	WHERE article_no = #{article_no}


UPDATE articletbl 
SET article_grouporder = article_grouporder + 1 
WHERE article_parentsno = #{article_parentsno} AND article_grouporder > #{article_grouporder}
